const example = msg.payload;
const year = parseInt(msg.selected_year);
const rawRows = example.split('\n');
const headers = rawRows[0].split(',');

const output = {
    'list': [],
    'byYear': {},
    'byMonth': {},
    'byWeek': {},
    'byReason': {},
    'byNeed': {},
    'departureRegions': {},
    'arrivalRegions': {},
    'departureDistricts': {},
    'arrivalDistricts': {},
    'total': 0,
};
const selectedYear = year;
rawRows.forEach(function (rawRow, rawRowIdx) {
    if (rawRowIdx !== 0) {
        const row = rawRow.split(',');
        const data = {};
        headers.forEach(function (value, idx) {
            if (value !== undefined && row[idx] !== undefined) {
                data[value.replace('\r', '')] = row[idx].replace('\r', '')
            }
        });
        output['list'].push(data);
        const people = parseInt(data['tpeople']);
        const yearData = parseInt(data['yr']);
        if (isNaN(yearData)) {
            return;
        }

        // format by year data
        {
            const dataKey = yearData;
            const outputKey = 'byYear';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }
        // format by week
        {
            const year = data['yr'];
            const week = data['weeknum'];
            const outputKey = 'byWeek';
            if (output[outputKey][year] === undefined) {
                output[outputKey][year] = {};
            }
            if (output[outputKey][year][week] === undefined) {
                output[outputKey][year][week] = 0;
            }
            output[outputKey][year][week] += people;
        }
        if (yearData !== selectedYear) {
            return
        }
        output['total'] += people;

        // by month
        {
            const dataKey = data['monthname'] + ' ' + data['yr'];
            const outputKey = 'byMonth';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }

        // by reason
        {
            const dataKey = data['creason'];
            const outputKey = 'byReason';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }

        // by needs
        {
            const dataKey = data['cneed'];
            const outputKey = 'byNeed';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }
        // by Departure Regions
        {
            const dataKey = data['pregion'];
            const outputKey = 'departureRegions';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }

        // by Departure Regions
        {
            const dataKey = data['cregion'];
            const outputKey = 'arrivalRegions';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }

        // by Departure District
        {
            const dataKey = data['pdistrict'];
            const outputKey = 'departureDistricts';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }

        // by Departure District
        {
            const dataKey = data['cdistrict'];
            const outputKey = 'arrivalDistricts';
            if (output[outputKey][dataKey] === undefined) {
                output[outputKey][dataKey] = 0;
            }
            output[outputKey][dataKey] += people;
        }
    }
});

msg.payload = output;
return msg;

