const dataByArea = msg.payload;
const administrative = JSON.parse(JSON.stringify(msg.administrative_2));
const colorPhase = [
    '',
    '#CDFACD',
    '#F9E61E',
    '#E67800',
    '#C80F00',
    '#640300'
]
administrative.features.forEach(function (value, idx) {
    const properties = value['properties'];
    const areaProperty = dataByArea[properties.admin2Name.toLowerCase()];
    if (areaProperty) {
        let currentHtml = '';
        let projectedHtml = '';
        colorPhase.forEach(function (color, idx) {
            if (idx !== 0) {
                {
                    const censusPopulation = areaProperty['estimated_population_current'];
                    const population = areaProperty['phase' + idx + '_C_population'];
                    const percent = ((population / censusPopulation) * 100).toFixed(0) + '%';
                    currentHtml += "<span title='" + population + "' style='display: inline-block; width: " + percent + "; background-color:" + color + "; overflow: hidden'>" + percent + "</span>";
                }
                {
                    const censusPopulation = areaProperty['estimated_population_projected'];
                    const population = areaProperty['phase' + idx + '_P_population'];
                    const percent = ((population / censusPopulation) * 100).toFixed(0) + '%';
                    projectedHtml += "<span title='" + population + "' style='display: inline-block; width: " + percent + "; background-color:" + color + "; overflow: hidden'>" + percent + "</span>";
                }
            }

        });
        value['properties'] = {
            'Title': areaProperty['title'],
            'Name': properties.admin2Name,
            'Population': areaProperty['census_population'],
            'Phase 3+': areaProperty['p3_plus_population'],
            'Percentage':
                "<div style='padding: 5px'>" +
                "<b>Current</b><br><div style='width: 275px'>" + currentHtml + "</div>" +
                "<b>Projected</b><br><div style='width: 275px'>" + projectedHtml + "</div>" +
                "</div>"
        };

        const phase = parseInt(areaProperty['overall_phase']);
        value['style'] = {
            'fill-color': colorPhase[phase],
            'fill-opacity': 1,
            'stroke-width': 1,
            'stroke': '#fff'
        }
    } else {
        value['style'] = {
            'fill-color': "grey",
            'fill-opacity': 1,
            'stroke-width': 1,
            'stroke': '#fff'
        }
    }
});
msg.payload = administrative;
